import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';

const columns = [
  { id: 'name', label: 'Items', minWidth: 170 },
  { 
    id: 'ac',
    label: 'Carton',
    minWidth: 10,
    align: 'middle',
    format: (value) => value.toLocaleString('en-US'),
  }, 
  {
    id: 'dz',
    label: 'Dozen',
    minWidth: 10,
    align: 'middle',
    format: (value) => value.toLocaleString('en-US'),
  },
  {
    id: 'piece',
    label: 'Piece',
    minWidth: 10,
    align: 'middle',
    format: (value) => value.toLocaleString('en-US'),
  },
  {
    id: 'cs',
    label: 'Carton Sales',
    minWidth: 10,
    align: 'middle',
    format: (value) => value.toLocaleString('en-US'),
  },
  {
    id: 'ds',
    label: 'Dozen Sales',
    minWidth: 10,
    align: 'middle',
    format: (value) => value.toLocaleString('en-US'),
  },
  {
    id: 'ps',
    label: 'Piece Sales',
    minWidth: 10,
    align: 'middle',
    format: (value) => value.toLocaleString('en-US'),
  },
  {
    id: 'cb',
    label: 'Carton Buy',
    minWidth: 10,
    align: 'middle',
    format: (value) => value.toLocaleString('en-US'),
  },
  {
    id: 'db',
    label: 'Dozen Buy',
    minWidth: 10,
    align: 'middle',
    format: (value) => value.toLocaleString('en-US'),
  },
  {
    id: 'pb',
    label: 'Piece Buy',
    minWidth: 10,
    align: 'middle',
    format: (value) => value.toLocaleString('en-US'),
  },
];

// ac amount in carton left
// dz dozen left
// piece left

function createData(name,ac,dz,sac,sadz,bac,badz) {
                    // n,ac,adz,s.ac,s.adz,b.ac,b.adz
  // createData(e[0],e[1],e[2],e[3],e[4],e[5])
  return { name,ac,dz,sac,sadz,bac,badz };
}

const rows = [];

const useStyles = makeStyles({
  root: {
    width: '100%',
  },
  container: {
    maxHeight: 440,
  },
});

export default function StickyHeadTable({sdata}) {
  const classes = useStyles();
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  useEffect(()=>{
    // n,ac,adz,s.ac,s.adz,b.ac,b.adz
    sdata.forEach(e => {

      rows.push(createData(e[0],e[1],e[2],e[3],e[4],e[5]))
    });
  })

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  useEffect()



  return (
    <Paper className={classes.root}>
      <TableContainer className={classes.container}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
              return (
                <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                  {columns.map((column) => {
                    const value = row[column.id];
                    return (
                      <TableCell key={column.id} align={column.align}>
                        {column.format && typeof value === 'number' ? column.format(value) : value}
                      </TableCell>
                    );
                  })}
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={rows.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </Paper>
  );
}
