import React from 'react';
import Appbar from './Appbar'
import Business from "./BusinessesMenus"
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import  { useState, useEffect } from "react";
import Confirmation from './Confirmation'
import Provison from "./Provision/FormandAnalysis"
import { BrowserRouter as R, Switch as S, Route, Link } from "react-router-dom";
import './App.css';


function App() {
  const [t, st] = React.useState("Massaquoi Royal");
  const [dt,setD] = useState({i:[],s:[]})
  const [open, setOpen] = React.useState(false);
     const [type,stype] = useState("Carton")
   const wf =  [{t:"Carton"},{t:"Dozen"},{t:"Piece"}]

  const f=(v)=>{
    st(v)
  }

  const hOpen = () => {
    setOpen(!open);
  };

  // to select in what form bough
  const stypes = (e)=>{
    stype(wf[e].t)
    console.log("yes")
  }


  const add = (v)=>{
    var t = true
    dt.i.forEach(e => {
      if(e.n==v.n){
           t=false
           hOpen()
      }    
    });

    if(t){
      setD({...dt,i:[...dt.i,v]})
    }

    // //  }


  }


  return (
    <div className="App"> 
     <Appbar t={t}/>  
      <R>
        <Route component={Business} exact path="/" />  
          <Route exact path="/provision" render={(props) => <Provison {...props} type={type} wf={wf} stypes={stypes}  a={add} itm={dt}  f={f} />} />   
      </R>
      <TextField id="standard-basic" label="Item Name:"   />
      <Button size="small" color="primary" onClick={hOpen} > 
            Save
          </Button>
 <ul>
       {
          dt.i.map(el=>(
            <li>
              {el.n}
            </li>
          ))
       } 
       </ul> 

       <Confirmation hOpen={hOpen} open={open}/>
    </div>
  );
}

export default App;
