import React from 'react';
import Appbar from './Appbar'
import Business from "./BusinessesMenus"
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import  { useState } from "react";
import Confirmation from './Confirmation'
import Provison from "./Provision/FormandAnalysis"
import { BrowserRouter as R,   Route } from "react-router-dom";
import './App.css';



function App() {
  const [t, st] = React.useState("Massaquoi Royal");
  const [dt,setD] = useState({i:[],s:[],b:[],d:''})
  const [open, setOpen] = React.useState(false);
     const [type,stype] = useState("carton")
   const wf =  [{t:"carton"},{t:"dozen"},{t:"piece"}]


   const [msg,smsg] = useState('record')
  //  const [bi,sbi] = useState([])
  const f=(v)=>{
    st(v)
  }

  const hOpen = () => {
    setOpen(!open);
  };

  // to select in what form bough
  const stypes = (e)=>{
    stype(e)

  }


  const add = (v)=>{

    var t = true

    dt.i.forEach(e => {
      if(v.n===e.n){
        t = false
      }
    });
      if(t){
      setD({...dt,i:[...dt.i,v]})
    }else{
      smsg('if you want to change this product ask admin. record exist')
      hOpen()  
    }


  }

  const sales =(v)=>{
    var s = dt.i

    for (let x = 0; x < s.length; x++) {
      // {n:'',p:'',t:'',q:''}
      if(s[x].n === v.n){
      if(v.t=== "carton" && s[x].c >= v.q){
        s[x].c = s[x].c*1  -   v.q*1

      }else  if(b[ii].t=== "dozen" ){
        let ai = (((s[x].aic*1)* (s[x].c*1))  + (((s[x].dz*1)*12)+s[x].pc))  - (v.q*1)*12
        let tc = ai/(s[x].aic*1)

        let rm = ai%(s[x].aic*1)

        s[x].dz= rm/12
        s[x].pc = rm%12        

      }else{
        let ai = (((s[x].aic*1)* (s[x].c*1))  + (((s[x].dz*1)*12)+s[x].pc))  - (v.q*1)
        let tc = ai/(s[x].aic*1)

        let rm = ai%(s[x].aic*1)

        s[x].dz= rm/12
        s[x].pc = rm%12  
      }

    }



  }
  setD({...dt,s:[...dt.s,v]})
  setD({...dt,i:s })
  }


const buy =(v)=>{     

  var b = [...dt.b,v]

    var i = dt.i

    for (let ii = 0; ii < b.length; ii++) {
     for (let jx = 0; jx < i.length; jx++) {
      //

    if(b[ii].n===i[jx].n ){  



//aic = amount in carton
//dz = amount of dozen
//pc = amount of piece
//c = amount of carton
//t = type or form
//q = quantity
//p = price
//
// const [n,sn] = useState({n:'',p:'',t:'',q:'',aic:'',dz:'',c:'',pc:''})

// {t:"carton"},{t:"dozen"},{t:"piece"}
// parseInt(5/3)
// 5
// {n:'',p:'',t:'',q:''}

// prdz = price for dozen
    // i[jx].prdz = b[ii].p
// prc = price for Carton
// prp = price for piece
      if(b[ii].t=== "carton" ){
        i[jx].c = b[ii].q*1 +   i[jx].c*1
        // i[jx].prc = b[ii].p
      }else  if(b[ii].t=== "dozen" ){
        i[jx].dz = b[ii].q*1 +   i[jx].dz*1
        // i[jx].prdz = b[ii].p
      }else{
        if(b[ii].q*1>=12){
          i[jx].dz =  parseInt((b[ii].q*1)/12)   +  i[jx].dz*1
          i[jx].pc = ((b[ii].q*1)%12) +   i[jx].pc*1
          // i[jx].prdz = (b[ii].p*1) *  12 
          // i[jx].prp = b[ii].p
        }else{
          i[jx].pc = b[ii].q*1 +   i[jx].pc*1
          // i[jx].prp = b[ii].p
        }
      }


      setD({...dt,i:i})
      setD({...dt,b:b})



     }

    } 

  }

}

  return (
    <div className="App"> 
     <Appbar t={t}/>  
      <R>
        <Route component={Business} exact path="/" />  
          <Route exact path="/provision" render={(props) => <Provison {...props}  buy={buy} type={type} wf={wf} stypes={stypes}  a={add} itm={dt}  f={f} sales={sales} />} />   
      </R>
      <TextField id="standard-basic" label="Item Name:"   />
      <Button size="small" color="primary" onClick={hOpen} > 
            Save
          </Button>
 <ul>
   Total
       {
          dt.i.map(el=>(
            <li>
              {`${el.n}  p: ${el.p} q: ${el.q} a: ${el.a}`}
            </li>
          ))
       } 
       </ul> 


       <ul>
   Buy
       {
          dt.b.map(el=>(
            <li>
              {`${el.n}  p: ${el.p} q: ${el.q} a: ${el.a}`}
            </li>
          ))
       } 
       </ul> 

       <Confirmation hOpen={hOpen} msg={msg} open={open}/>
    </div>
  );
}

export default App;
