     if(i[p].n===v.n){
      if(v.t=== "carton" ){
        i[p].gt =  (i[p].gt*1) -  ((i[p].aic*1) * v.q )       
        aic[v.n] = i[p].aic*1
        b[p].ac = b[p].ac-v.q

