import React from 'react';
import Appbar from './Appbar'
import Business from "./BusinessesMenus"
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import  { useState } from "react";
import Confirmation from './Confirmation'
import Provison from "./Provision/FormandAnalysis"
import { BrowserRouter as R,   Route } from "react-router-dom";
import './App.css';



function App() {
  const [t, st] = React.useState("Massaquoi Royal");
  const [dt,setD] = useState({i:[],s:[],b:[],d:''})
  const [open, setOpen] = React.useState(false);
     const [type,stype] = useState("carton")
   const wf =  [{t:"carton"},{t:"dozen"},{t:"piece"}]


   const [msg,smsg] = useState('record')
  //  const [bi,sbi] = useState([])
  const f=(v)=>{
    st(v)
  }

  const hOpen = () => {
    setOpen(!open);
  };

  // to select in what form bough
  const stypes = (e)=>{
    stype(e)

  }


  const add = (v)=>{

    var t = true

    dt.i.forEach(e => {
      if(v.n===e.n){
        t = false
      }
    });
      if(t){
      setD({...dt,i:[...dt.i,v]})
    }else{
      smsg('if you want to change this product ask admin. record exist')
      hOpen()  
    }


  }

  const sales =(v)=>{
    var s = dt.i

    for (let x = 0; x < s.length; x++) {
      // {n:'',p:'',t:'',q:''}
      if(s[x].n === v.n){
      if(v.t=== "carton" && s[x].c >= v.q){
        s[x].gt = s[x].gt*1  -   ((s[x].aic*1)*v.q*1)

      //gt = grand total
// prdz = price for dozen
// prc = price for Carton
// prp = price for piece

      }else  if(v.t=== "dozen" ){
        let ai = (((s[x].aic*1)* (s[x].c*1))  + (((s[x].dz*1)*12)+s[x].pc))  - (v.q*1)*12
        s[x].c = ai/(s[x].aic*1)

        let rm = ai%(s[x].aic*1)

        s[x].dz= rm/12
        s[x].pc = rm%12        

      }else{
        let ai = (((s[x].aic*1)* (s[x].c*1))  + (((s[x].dz*1)*12)+s[x].pc))  - (v.q*1)
        s[x].c = ai/(s[x].aic*1)

        let rm = ai%(s[x].aic*1)

        s[x].dz= rm/12
        s[x].pc = rm%12  
      }

    }



  }
  setD({...dt,s:[...dt.s,v]})
  setD({...dt,i:s })
  }


const buy =(v)=>{     

  var b = dt.b

    var i = dt.i
  var exit = true
    b.forEach(e => {
      if(e.n===v.n){
        exit = false
      } 
         e.sts = 'o'

    });

    if(exit){
      v.sts = 'n'
      b = [...dt.b,v]
    }

    var aic={}
    for (let p = 0; p < i.length; p++) {
     if(i[p].n===v.n){
      if(v.t=== "carton" ){
        i[p].gt =  (i[p].gt*1) +  ((i[p].aic*1) * v.q )       
        aic[v.n] = i[p].aic*1
      }else  if(v.t=== "dozen" ){
        i[p].gt =  (i[p].gt*1) +   (v.q *12)        
      }else{
        i[p].gt =  (i[p].gt*1) +   (v.q *1)        
      }
     }      
    }






    for (let q = 0; q <b.length; q++) {
     if(b[q].n===v.n){
      if(b[q].sts==='n'){
       if(v.t=== "carton" ){
         b[q].q =   aic[v.n] * (v.q*1)    
         b[q].prc = v.p    
       }else  if(v.t=== "dozen" ){
        b[q].q = (v.q*1) * 12 
        b[q].prdz = v.p
       }else{
        b[q].q =   (v.q*1) 
        b[q].prp = v.p
       }
      } else{
        if(v.t=== "carton" ){
          b[q].q =   (b[q].q*1) + (aic[v.n] * (v.q*1))  
          b[q].prc = v.p  
        }else  if(v.t=== "dozen" ){
          b[q].q =  (b[q].q*1) + ((v.q*1) * 12)
          b[q].prdz = v.p
        }else{
          b[q].q =  (b[q].q*1) +  (v.q*1) 
          b[q].prp = v.p
        }
      }     
     }

    }
    // {n:'',p:'',t:'',q:''}
    //gt = grand total
// prdz = price for dozen
// prc = price for Carton
// prp = price for piece


      setD({...dt,i:i})
      setD({...dt,b:b})



     }





  return (
    <div className="App"> 
     <Appbar t={t}/>  
      <R>
        <Route component={Business} exact path="/" />  
          <Route exact path="/provision" render={(props) => <Provison {...props}  buy={buy} type={type} wf={wf} stypes={stypes}  a={add} itm={dt}  f={f} sales={sales} hOpen={hOpen} ms={smsg}/>} />   
      </R>
      <TextField id="standard-basic" label="Item Name:"   />
      <Button size="small" color="primary" onClick={hOpen} > 
            Save
          </Button>
 <ul>
   Total
       {
          dt.i.map(el=>(
            <li>
              {`${el.n}  p: ${el.p} q: ${el.q} a: ${el.a}`}
            </li>
          ))
       } 
       </ul> 


       <ul>
   Buy
       {
          dt.b.map(el=>(
            <li>
              {`${el.n}  p: ${el.p} q: ${el.q} a: ${el.a}`}
            </li>
          ))
       } 
       </ul> 

       <Confirmation hOpen={hOpen} msg={msg} open={open}/>
    </div>
  );
}

export default App;
