import React,{useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';

import Accordion from '@material-ui/core/Accordion';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionActions from '@material-ui/core/AccordionActions';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    margin:.5,
    marginTop:2
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
  },
  secondaryHeading: {
    fontSize: theme.typography.pxToRem(15),
    color: theme.palette.text.secondary,
  },
  icon: {
    verticalAlign: 'bottom',
    height: 20,
    width: 20,
  },
  details: {
    alignItems: 'center',
  },
  column: {
    flexBasis: '33.33%',
  }, 
  act: {
    display:'flex',
    flexDirection:'column',
    flexWrap:'nowrap',
  }
}));

export default function DetailedAccordion({a }) {
 const [n,sn] = useState({n:'',p:'',t:'',q:'',a:'',dz:''})
 const [ins,sins] = useState('')
  const classes = useStyles();
const add = ()=>{ 
  a(n)
}

const ad = (e)=>{
  //  d = e.target.value
  if(e.target.name === 'n'){
    sn({...n,n:e.target.value})
    sins(e.target.value)
  }else{
    sn({...n,dz:e.target.value})
  }


}
  return (
    <div className={classes.root}>
      <Accordion defaultExpanded={false}>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1c-content"
          id="panel1c-header"
        >
          <div className={classes.column}>
            <Typography className={classes.heading}>New Item</Typography>
          </div>
          <div className={classes.column}>
            <Typography className={classes.secondaryHeading}>enter new item</Typography>
          </div>
        </AccordionSummary>
        <AccordionDetails className={classes.details}>
        <TextField  label="Item Name:" name='n' onChange={ad}  />
        <TextField  label={`How many ${ins} inside a carton`} onChange={ad}  />
        </AccordionDetails>
        <Divider />
        <AccordionActions className={classes.act}>
          <Button size="small">Cancel</Button>

          <Button size="small" color="primary" onClick={add}>
            Save
          </Button>
        </AccordionActions>
      </Accordion>
    </div>
  );
}
