import React from 'react';
import Appbar from './Appbar'
import Business from "./BusinessesMenus"
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import  { useState, useEffect } from "react";
import Provison from "./Provision/FormandAnalysis"
import { BrowserRouter as R, Switch as S, Route, Link } from "react-router-dom";
import './App.css';
import { Subject } from '@material-ui/icons';

function App() {
  const [t, st] = React.useState("Massaquoi Royal");
  const [dt,setD] = useState({i:[],s:[]})
 const [j,sj] = useState()

  const f=(v)=>{
    st(v)
  }

  const jj = (v)=>{
    sj(v)
  }

  const add = (v)=>{
    // var t = true
    // dt.i.forEach(e => {
    //   t=false
    // });
    //  console.log(v)
    //  var dd = dt.i
    // //  while(v==""){

     setD({...dt,i:[...dt.i,j]})
     console.log(dt)
    // //  }


  }

  const ad = (e)=>{
    sj(e.target.value)
  }
  return (
    <div className="App"> 
     <Appbar t={t}/>  
      <R>
        <Route component={Business} exact path="/" />  
          <Route exact path="/provision" render={(props) => <Provison {...props} j={jj}  a={add}  f={f} />} />   
      </R>
      <TextField id="standard-basic" label="Item Name:" onChange={ad}  />
      <Button size="small" color="primary" onClick={add}>
            Save
          </Button>
 <ul>
       {
          dt.i.map(el=>{
            <li>
              {el}
            </li>
          })
       } </ul> 
    </div>
  );
}

export default App;
