import React,{useState}  from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import Accordion from '@material-ui/core/Accordion';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionActions from '@material-ui/core/AccordionActions';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';
import Autocomplete from '@material-ui/lab/Autocomplete';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    margin:.5
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
  },
  secondaryHeading: {
    fontSize: theme.typography.pxToRem(15),
    color: theme.palette.text.secondary,
  },
  icon: {
    verticalAlign: 'bottom',
    height: 20,
    width: 20,
  },
  details: {
    display:'flex',
    alignItems: 'center',
    justifyContent:'center',
    flexDirection:'column',
    flexWrap:'nowrap'
  },
  column: {
    flexBasis: '33.33%',
  },
  div:{

    width:`100%`
  }


}));

export default function DetailedAccordion({itm,options,type,stypes}) {
  const classes = useStyles();
  const [n,sn] = useState({n:'',p:'',t:'',q:'',a:''})


  // item names
  const defaultProps = {
    options: itm,
    getOptionLabel: (option) => option.n,
  };

  // item type
  const defaultProp = {
    options: options,
    getOptionLabel: (option) => option.t,
  };

const add = ()=>{ 
  // a(n)
}

const types = (e)=>{
console.log(e)
  if(e!==" "){
   stypes(e) 
  }

}

const ad = (e)=>{
  //  d = e.target.value
 sn({...n,n:e.target.value}) 

}

  return (
    <div className={classes.root}>
      <Accordion defaultExpanded={false}>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1c-content"
          id="panel1c-header"
        >
          <div className={classes.column}>
            <Typography className={classes.heading}>Buy Entry</Typography>
          </div>
          <div className={classes.column}>
            <Typography className={classes.secondaryHeading}>enter buy record</Typography>
          </div>
        </AccordionSummary>

        <AccordionDetails className={classes.details}>
          {/* item */}
        <div className={classes.div}>
          <Autocomplete
          {...defaultProps}   
          renderInput={(params) => <TextField name={'n'} {...params} label="Item Name:" margin="normal" />}
          />
        </div>

        <div  className={classes.div}>
          <Autocomplete
         onChange={(event, value) =>  types(value.t)} 
            {...defaultProp}          
            renderInput={(params) => <TextField  {...params} name={'t'} label="What Form:" margin="normal" />}
          />

        </div>

     <TextField  label={`How much for one ${type}`} name={'p'} onChange={ad}  />

     <TextField  label={`How much ${type} you buy` } name={'q'} onChange={ad}  />

      {type != "Piece" && <TextField  label={`Amount inside the ${type}`} name={'p'} onChange={ad}  /> }




        {/* const [n,sn] = useState({n:'',p:'',t:'',q:'',a:''}) */}


        </AccordionDetails>
        <Divider />
        <AccordionActions>
          <Button size="small">Cancel</Button>
          <Button size="small" color="primary">
            Save
          </Button>
        </AccordionActions>
      </Accordion>

    </div>
  );
}
