import React from 'react';
import Appbar from './Appbar'
import Business from "./BusinessesMenus"
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import  { useState } from "react";
import Confirmation from './Confirmation'
import Provison from "./Provision/FormandAnalysis"
import { BrowserRouter as R, Switch as S, Route, Link } from "react-router-dom";
import './App.css';
import { blue } from '@material-ui/core/colors';


function App() {
  const [t, st] = React.useState("Massaquoi Royal");
  const [dt,setD] = useState({i:[],s:[],b:[],d:''})
  const [open, setOpen] = React.useState(false);
     const [type,stype] = useState("carton")
   const wf =  [{t:"carton"},{t:"dozen"},{t:"piece"}]
   const [ab,sab] = useState([])
  //  const [bi,sbi] = useState([])
  const f=(v)=>{
    st(v)
  }

  const hOpen = () => {
    setOpen(!open);
  };

  // to select in what form bough
  const stypes = (e)=>{
    stype(e)

  }


  const add = (v)=>{

      if(t){
      setD({...dt,i:[...dt.i,v]})
    }





  }


const buy =(v)=>{    



    var b = [...dt.b,v]
    var i = dt.i

    for (let ii = 0; ii < b.length; ii++) {
     for (let jx = 0; jx < i.length; jx++) {
       if(b[ii].n===i[jx].n && ab.indexOf(i[jx].n) ===-1){
         sab(i[jx].n)
        i[jx].p = b[ii].p*1 + i[jx].p*1
        i[jx].q = b[ii].q*1 +   i[jx].q*1
        if( i[jx].a!==''){
          i[jx].a = b[ii].a*1 +i[jx].a*1
        }
       }

     }

    } 

setD({...dt,i})
setD({...dt,b})

}

  return (
    <div className="App"> 
     <Appbar t={t}/>  
      <R>
        <Route component={Business} exact path="/" />  
          <Route exact path="/provision" render={(props) => <Provison {...props} buy={buy} type={type} wf={wf} stypes={stypes}  a={add} itm={dt}  f={f} />} />   
      </R>
      <TextField id="standard-basic" label="Item Name:"   />
      <Button size="small" color="primary" onClick={hOpen} > 
            Save
          </Button>
 <ul>
   Total
       {
          dt.i.map(el=>(
            <li>
              {`${el.n}  p: ${el.p} q: ${el.q} a: ${el.p}`}
            </li>
          ))
       } 
       </ul> 


       <ul>
   Buy
       {
          dt.b.map(el=>(
            <li>
              {`${el.n}  p: ${el.p} q: ${el.q} a: ${el.p}`}
            </li>
          ))
       } 
       </ul> 

       <Confirmation hOpen={hOpen} open={open}/>
    </div>
  );
}

export default App;
