import React from 'react';
import Appbar from './Appbar'
import Business from "./BusinessesMenus"
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import  { useState, useEffect } from "react";
import Confirmation from './Confirmation'
import Provison from "./Provision/FormandAnalysis"
import { BrowserRouter as R, Switch as S, Route, Link } from "react-router-dom";
import './App.css';


function App() {
  const [t, st] = React.useState("Massaquoi Royal");
  const [dt,setD] = useState({i:[],s:[]})
  const [open, setOpen] = React.useState(false);

  const f=(v)=>{
    st(v)
  }

  const hOpen = () => {
    setOpen(!open);
  };

  const add = (v)=>{
    var t = true
    dt.i.forEach(e => {
      if(e.n==v.n){
           t=false
      }

    });




     setD({...dt,i:[...dt.i,v]})
     console.log(dt)
    // //  }


  }


  return (
    <div className="App"> 
     <Appbar t={t}/>  
      <R>
        <Route component={Business} exact path="/" />  
          <Route exact path="/provision" render={(props) => <Provison {...props}  a={add}  f={f} />} />   
      </R>
      <TextField id="standard-basic" label="Item Name:"   />
      <Button size="small" color="primary" onClick={hOpen} > 
            Save
          </Button>
 <ul>
       {
          dt.i.map(el=>(
            <li>
              {el.n}
            </li>
          ))
       } 
       </ul> 

       <Confirmation o={handleOpen} open={open}/>
    </div>
  );
}

export default App;
