import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';

import Accordion from '@material-ui/core/Accordion';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import AccordionSummary from '@material-ui/core/AccordionSummary';
 import Table from './AnalysisT'
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';



const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%'
    ,
    margin:.5,
    marginTop:2
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
  },
  secondaryHeading: {
    fontSize: theme.typography.pxToRem(15),
    color: theme.palette.text.secondary,
  },
  icon: {
    verticalAlign: 'bottom',
    height: 20,
    width: 20,
  },
  details: {
    alignItems: 'center',
  },
  column: {
    flexBasis: '33.33%',
  },
  helper: {
    borderLeft: `2px solid ${theme.palette.divider}`,
    padding: theme.spacing(1, 2),
  },
  link: {
    color: theme.palette.primary.main,
    textDecoration: 'none',
    '&:hover': {
      textDecoration: 'underline',
    },
  },
}));

export default function DetailedAccordion({itm}) {
  const classes = useStyles();
  const [sdata,setsdata] = React.useState([])


  useEffect(()=>{
    const arr = []
if(itm.b.length || itm.s.length || itm.i.length  ){
  // const [dt,setD] = useState({i:[],s:[],b:[],d:''})
  // const [n,sn] = useState({n:'',p:'',t:'',q:'',sts:'',prdz:'',prc:'',prp:'',ac:'',adz:''})

  // return { name,ac,dz, piece,cs,ds,ps,cb,db,pb };
  for (let c = 0; c < itm.i.length; c++) {
    arr[c] = []
    arr[c].push(itm.i[c].n,(itm.i[c].ac||0), (itm.i[c].adz||0))

    console.log(arr)

    for (let s = 0; s < itm.s.length; s++) {
      if(arr[c][0] === itm.b[s].n){
        arr[c].push(itm.b[s].ac)
        arr[c].push(itm.b[s].adz)
      }    
    }   

    for (let b = 0; b < itm.b.length; b++) {
      if(arr[c][0] === itm.b[b].n){
        arr[c].push(itm.b[b].ac)
        arr[c].push(itm.b[b].adz)
      }    
    }
  }
}

setsdata(arr)

  },[itm.b.length, itm.s.length, itm.i.length])

//else{
//   return <p>no item found recorded </p>  
// } 



  return (
    <div className={classes.root}>
      <Accordion defaultExpanded={true}>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1c-content"
          id="panel1c-header"
        >
          <div className={classes.column}>
            <Typography className={classes.heading}> </Typography>
          </div>
          <div className={classes.column}>
            <Typography className={classes.secondaryHeading}>view analysis</Typography>
          </div>
        </AccordionSummary>
        <AccordionDetails className={classes.details}>
        <Table sdata={sdata}/>

        </AccordionDetails>

      </Accordion>
    </div>
  );
}
