      }else{
        i[p].gt =  (i[p].gt*1) +   (v.q *1)        
      }
     }      
    }






    for (let q = 0; q <b.length; q++) {
     if(b[q].n===v.n){     
       if(v.t=== "carton" ){
         b[q].q =   (v.q*1)    
         b[q].prc = v.p 
         b[q].ac =  v.q
       }else  if(v.t=== "dozen" ){
        b[q].q = (v.q*1)  
        b[q].prdz = v.p
        b[q].adz =   v.q*1
       }else{
        b[q].q =   (v.q*1) 
